        <>
        <div className="container">
        <div className="box">
            <h1>To do List</h1>
            <input placeholder="Add item to list"
                onChange={ChangedEvent}
            />
            <button onClick={clickedButton}>+</button>

            <ol className="List"> 
            
            </ol>
        </div>
        </div>
        </>
    )
}
